import java.io.*; import java.util.*; public class Solution { public static void main(String[] args) { Scanner sc = new Scanner(System.in); int arrLen = sc.nextInt(); int arr[] = new int[arrLen]; for (int i = 0; i < arrLen; i++) { arr[i] = sc.nextInt(); } int maxLeft[] = new int[arr.length]; int maxRight[] = new int[arr.length]; int m = 0; for (int i = 0; i < arr.length; i++) { if (arr[i] > m) { m = arr[i]; } maxLeft[i] = m; } m = 0; for (int i = arr.length - 1; i > -1; i--) { if (arr[i] > m) { m = arr[i]; } maxRight[i] = m; } int ans = 0; for (int i = 0; i < maxRight.length; i++) { ans += (Integer.min(maxLeft[i], maxRight[i]) - arr[i]); } System.out.println(ans); } }